$customizer_settings = mura_general_settings();

$primary_theme_color = get_theme_mod( 'mura_primary_theme_color', $customizer_settings['primary_theme_color'] );
$black = '#000000';
$white = '#ffffff';
$off_white = '#f7f8fa';
$muted_text_color = '#94979e';

$content_max_width       = absint( $this->get( 'content_max_width' ) );

