$header_toggle_color     = '' === get_theme_mod( 'mura_header_toggle_icon_color', '' ) ? $black : get_theme_mod( 'mura_header_toggle_icon_color', '' );
$footer_background 		 = '' === get_theme_mod( 'mura_footer_background', '' ) ? $off_white : get_theme_mod( 'mura_footer_background', '' );
$button_color            = '' === get_theme_mod( 'mura_button_color', '' ) ? $white : get_theme_mod( 'mura_button_color', '' );
$button_background       = '' === get_theme_mod( 'mura_button_background', '' ) ? $primary_theme_color  : get_theme_mod( 'mura_button_background', '' );
$logo_width = get_theme_mod( 'mura_custom_logo_max_width_mobile', 100 );
$logo_width_small = get_theme_mod( 'mura_custom_logo_max_width_small', 70 );
$toggle_sidebar_background = '' === get_theme_mod( 'mura_toggle_sidebar_background', '' ) ? $white  : get_theme_mod( 'mura_toggle_sidebar_background', '' );
$toggle_sidebar_menu_color = '' === get_theme_mod( 'mura_toggle_sidebar_menu_color', '' ) ? $black  : get_theme_mod( 'mura_toggle_sidebar_menu_color', '' );

$alignwide_max = $content_max_width > 0 ? $content_max_width * 2 : 1920

